function [H] = harris(Ix,Iy, sigmag)       k = 0.05; % constante appartenant à [0.04;0.06] (empiriquement)        % Lissage des images Ix2, Iy2, Ixy (application filtre gaussien)    Gauss = gaussian(sigmag);        Ix2 = Ix.^2;    Iy2 = Iy.^2;    Ixy = Ix.*Iy;        Sxx=conv2(Ix2,Gauss,'same');     Syy=conv2(Iy2,Gauss,'same');     Sxy=conv2(Ixy,Gauss, 'same');    %     % Opérateur de Harris (det(C) - tr(C)^2)     H = Sxx.*Syy-Sxy.^2 - k*(Sxx+Syy).^2;     end